#!/usr/bin/env python3

dna = ''

indiv_nt = list(dna)

indiv_it = iter(indiv_nt)


tallyA = 0
tallyG = 0
tallyC = 0
tallyT = 0


for N in indiv_it:
	if N == 'A':
		tallyA += 1
	elif N == 'G':
		tallyG += 1
	elif N == 'C':
		tallyC += 1
	else:
		tallyT += 1

nt_comp = {}

nt_comp['A'] = tallyA
nt_comp['G'] = tallyG
nt_comp['C'] = tallyC
nt_comp['T'] = tallyT

print(nt_comp)

GCcontent = ((tallyG + tallyC)/(tallyG + tallyC + tallyA + tallyT))
print('GC content is', GCcontent*100)








